import './App.css';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import React, {Component} from 'react';
import characters from './db/Character/Character';
import episode from './db/Episode/Episode';
import qoutes from './db/Qoutes/Qoutes';
import death from './db/Death/Deaths';

const combine = characters.concat(episode).concat(qoutes).concat(death);
const query = combine;

// importing screens display
import Home from './screen/Home';
import Character from './screen/Character';
import Episode from './screen/Episode';
import Quote from './screen/Qoutes';
import Death from './screen/Death';


// importing navbar component
import Navbar from "./components/Navbar";



class App extends Component {
    constructor(){
      super()
      this.state = {
        query: query,
        searchField: ""
      }
    }

    onsearchChange = (event) => {
      this.setState({searchField:event.target.value});
    }

    render(){
      const filterSearch = this.state.query.filter(query => {
        return query.name.toLowerCase().includes(this.state.searchField.toLowerCase())
      });

    return (
      <Router>
        <Navbar />
            <main>
                <Routes>
                        <Route path="/home" element={<Home />} />
                        <Route path="/character" element={<Character />} />
                        <Route path="/episode" element={<Episode />} />
                        <Route  path="/qoutes" element={<Quote />} />
                        <Route  path="/death" element={<Death />} />
                </Routes>
            </main>


      </Router>
    );
}
}


export default App;
