import "./Style.css";
import characters from "../db/Character/Character";
import React, { Component } from "react";


class Character extends Component {
    constructor(){
        super()
        this.state ={
            query: list,
        }
    }
    render() {
    const data = characters;
    const dataItem = data.map((list) => (
        <li className="drap">
            <br/>{list.char_id} <br/>
            {list.name}<br/>
            {list.birthday}<br/>
            {list.occupation}<br/>
            {list.img}<br/>
            {list.status}<br/>
            {list.appearance}<br/>
            {list.nickname}<br/>
            {list.portrayed}<br/>
            {list.better_call_saul_appearance}
         </li>
    ));
    return (
        <div>
            <div>
              <h2>Characters Record</h2>
          </div>
          <div className="input">
        <input type="search" placeholder="Input what to find"/>
    </div>
            <p className="drup">{dataItem}{}</p>
        </div>
    );
}
}

export default Character;
