import "./Style.css";
import characters from "../db/Character/Character";
import React, { Component } from "react";
import SearchBox from "./SearchBox";

class Character extends Component {
    constructor(){
        super()
        this.state ={
            query: characters,
            searchfield: ""
        }
    }
    onSearchChange = (event) => {
        this.setState({searchfield: event.target.value });
        }
        render() {
            const filterQuery = this.state.query.filter(query => {
                return query.name.toLowerCase().includes(this.state.searchfield.toLowerCase())
            });

            const data = characters;
            const dataItem = data.map((list) => (
                <li className="drap">
                    <br/>{list.char_id} <br/>
                    {list.name}<br/>
                    {list.birthday}<br/>
                    {list.occupation}<br/>
                    {list.img}<br/>
                    {list.status}<br/>
                    {list.appearance}<br/>
                    {list.nickname}<br/>
                    {list.portrayed}<br/>
                    {list.better_call_saul_appearance}
                </li>
    ));
    return (
        <div>
            <div>
              <h2>Characters Record</h2>
            </div>
         
            <SearchBox searchChange ={this.onSearchChange} />

            <p className="drup">{}</p>
            
        </div>
    );
}
}

export default Character;
